import logo from './logo.svg';
import './App.css';
import React,{ Component, useState, useEffect } from 'react';
import axios from 'axios';

const [placeholder, setPlaceholder] = useState('Hi');
useEffect(() => { 
    fetch('/upload').then(res => res.json()).then(data => {
      setPlaceholder(data.result);
    });
  }, []);
class App extends Component{

selecetedFile = event=>{
this.setState({
selecetedFile: event.target.files[0]
})
}

dosyaUpload= () =>{
const fd = new FormData();
fd.append('image',this.state.selecetedFile,this.state.selecetedFile.name);
axios.post("/upload",fd);

}


render(){ return(<div className="App">)
<p>Flask says {placeholder}</p>
<input type="file" onChange={this.selecetedFile}/>
<button onClick={this.dosyaUpload}>Upload</button>
</div>);

}





}
export default App;
